M.local_corusefiles={};M.local_corusefiles.dialog={};M.local_corusefiles.Y=null;M.local_corusefiles.init_dialog=function(Y){Y.use("panel","io-base",function(Y){var dialog=new Y.Panel({contentBox:Y.Node.create('<div id="c1n_dialog" />'),headerContent:"Empaquetando, por favor espere...",bodyContent:'<img src="'+M.cfg.wwwroot+'/local/c1n_download_coursefiles/img/rel_interstitial_loading.gif" />',width:240,zIndex:6,align:{node:'#page-content',points:[Y.WidgetPositionAlign.TC,Y.WidgetPositionAlign.TC]},modal:!0,render:'.example',visible:!1,});M.local_corusefiles.dialog=dialog;M.local_corusefiles.Y=Y})};M.local_corusefiles.download=function(Y,courseid){var Y=M.local_corusefiles.Y;M.local_corusefiles.dialog.show();var cfg={method:'GET',data:'id='+courseid,on:{success:function(id,objData){M.local_corusefiles.dialog.hide();location.href=objData.responseText},failure:function(){M.local_corusefiles.dialog.hide();alert('Error en la transacci√≥n')}},};var request=Y.io(M.cfg.wwwroot+'/local/c1n_download_coursefiles/c1n_download_coursefiles.php',cfg)}